from __future__ import unicode_literals
import os
import sys

# add parent directory to python path
# to enable import of tested package
sys.path.append(os.path.join(os.getcwd(), '../'))


def assetpath(path):
    return os.path.join(
        os.path.dirname(__file__),
        'assets',
        path)
