from __future__ import unicode_literals
import pytest
from seasonvar import Requester
assert pytest


def test_requester_set_ipad_useragent():
    requester = Requester()
    useragent = requester.session.headers['User-Agent']
    assert useragent == 'Mozilla/5.0 (iPad; CPU OS 9_1 like Mac OS X) '\
                        'AppleWebKit/601.1.46 (KHTML, like Gecko) '\
                        'Version/9.0 Mobile/13B143 Safari/601.1'
